import React, {createContext, useState}  from 'react';
import MainPage from '../main-page/main-page';
import {booksPropTypes} from '../../common/prop-types.js';
import {AppRoute} from '../../common/const';
import {Routes, Route, BrowserRouter as Router} from 'react-router-dom';
import NotFoundScreen from '../not-found-screen/not-found-screen';
import SignIn from '../sign-in/sign-in';
import BookPage from '../book-page/book-page';
import Reader from '../reader/reader';
import browserHistory from "../../browser-history";

export const ThemeContext = createContext(null);

const App = () => {

  const toggleTheme = () => {
    setTheme((curr) => (curr== "light-theme" ? "dark-theme" : "light-theme"));
  }
  const [theme, setTheme] = useState("dark-theme");

  return (
        <ThemeContext.Provider store={store} value={{theme, toggleTheme}}>
        <div className={theme}>

    <Router history={browserHistory}>
      <Routes>
        <Route exact path={AppRoute.MAIN} element={<MainPage  />}/>
        <Route exact path={AppRoute.LOGIN} element={<SignIn />} />
        <Route exact path={AppRoute.BOOKPAGE} element={ <BookPage />} />
        <Route exact path={AppRoute.READER} element={ <Reader />} />
        <Route exact path={AppRoute.ERROR} element={<NotFoundScreen />} />
        <Route path="*" element={<NotFoundScreen />} />
      </Routes>
    </Router>

       </div>
       </ThemeContext.Provider>
  );
};

App.propTypes = {
  books: booksPropTypes
};
export default App;
