import React from 'react';
import {booksPropTypes} from '../../common/prop-types.js';
import BooksList from '../books-list/books-list';
import Header from '../header/header';
import Footer from '../footer/footer';
import {connect} from 'react-redux';

const MainPage = (props) => {

	const {books} = props;
 return (
		<>
			<div className="page light-theme">
				<div className="main">
					<Header />
				<div className="books">
					<BooksList books={books} bookName="MAIN"/>
				</div>
					<Footer />	
			 </div>
				<button className="up-button" type="button">
					↑
					<span className="visually-hidden">Наверх</span>
				</button>
		</div>
	</>
	)
}

MainPage.propTypes = {
	books: booksPropTypes
};

const mapStateToProps = (state) => ({
	books: state.books,
	activeBook: state.activeBook,
});

const mapDispatchToProps = (dispatch) => ({
	OnMainPageRender() {
		 dispatch(ActionCreator.getBooks());
	},
});

export {MainPage};
export default connect(mapStateToProps, mapDispatchToProps)(MainPage);
