import React from 'react';
import {booksPropTypes} from '../../common/prop-types.js';
import {getBook} from '../../common/utils.js';
import {Link} from 'react-router-dom';


const BookMenu = (props) => {
  const {book, setActiveBook, unsetActiveBook} = props;

  const {
   id,
   title,
   tags,
   image,
   price,
   description
 } = book;


	const mouseOver = () => setActiveBook(book.id);
	const mouseOut = () => unsetActiveBook(); 


  return (
   <li onMouseOver={mouseOver} onMouseOut={mouseOut}><Link to={getBook(book.id)}>{book.title}</Link></li>
  );
};

Book.propTypes = {
  book: booksPropTypes
};

export default BookMenu;
