import {ActionType} from './action';
import {getBooks} from '../common/utils';
import {DEFAULT_BOOK, AuthorizationStatus} from '../common/const';
import books from '../mocks/books';
import reviews from '../mocks/reviews';
import readers from '../mocks/readers';

const initialState = {
  activeBookId : null,
  isDataLoaded : false,
  books,
  reviews,
  readers,
  bookPage: {},
  isBookLoaded: false,
  fontSize: 14,
  authorizationStatus: NO_AUTH,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionType.CHANGE_BOOK:
      return {
        ...state,
        activeBook: action.payload
      };


    case ActionType.GET_READERS:
      return {
        ...state,
        readers: action.payload,
      };
    case ActionType.GET_BOOKS:
      return {
        ...state,
        books: initialState.books,
        isDataLoaded: true,
      };
    case ActionType.GET_BOOK_PAGE:
      return {
        ...state,
        bookPage: action.payload,
        isBookLoaded: true
      };
    case ActionType.GET_ACTIVE_BOOK:
      return {
        ...state,
        activeBookId: action.payload,
      };
    case ActionType.GET_REVIEWS:
      return {
        ...state,
        reviews: action.payload,
      };
    case ActionType.CHANGE_FONT_SIZE:
      return {
        ...state,
        fontSize: action.payload,
      };
    case ActionType.REQUIRED_AUTHORIZATION:
      return {
        ...state,
        authorizationStatus: action.payload,
        };
  }

  return state;
};

export {reducer};
