import {AppRoute} from "./const";

export const getBook = (id) => AppRoute.BOOKPAGE.replace(/:id/, id);
export const getBookReader = (id) => AppRoute.READER.replace(/:id/, id);

export const getReaderFromBook = (readers, selectedBook) => {
 return readers.filter((reader) => reader.book.name === selectedBook);
};

export const getBooks = (offers) => {
 return offers;
}
