import React from 'react';
import { AppRoute } from '../../common/const';
import { Link } from 'react-router-dom';
import {connect} from 'react-redux';
import {getBook} from '../../common/utils.js';

const Header = (props) => {

const {
  books,
		setActiveBook, 
		unsetActiveBook,
		activeBookId
	} = props;

	const book = books.find((bookn) => bookn.id == activeBookId);



		const mouseOver = () => setActiveBook(book.id);
		const mouseOut = () => unsetActiveBook(); 

	return (
		<div className="page-header">
			<div className="nav">
				<ul className="main-menu">
					<li className="top"><Link to={AppRoute.MAIN}>Главная</Link></li>
					<li className="open"> <a href="#">Виктория ОМ</a>
						<ul className="sub-menu">
						  {books.map((book) => (
								  <li key={book.id} onMouseOver={mouseOver} onMouseOut={mouseOut}><Link to={getBook(id)}>{book.title}</Link></li>
								))}
						</ul>
					</li>
					<li><a href="#">Моя библиотека</a></li>
				</ul>
			</div>

			<div className="top-panel">
				<form action="" method="POST">
					<input type="search" name="search" id="search" placeholder="Найти" />
				</form>
				<a href="/registration">Регистрация/</a>
				<a href="/login">Вход</a>
				<button className="theme-button" type="button">Изменить тему</button>
			</div>
		</div>
	)
};

const mapStateToProps = (state) => ({
	books: state.books,
	activeBookId: state.activeBookId,
});

const mapDispatchToProps = (dispatch) => ({
  setActiveBook(id) {
    dispatch(ActionCreator.getActiveBook(id));
  },
  unsetActiveBook() {
    dispatch(ActionCreator.getActiveBook(null));
  }
});

export {Header};
export default connect(mapStateToProps, mapDispatchToProps)(Header);
