import React from 'react';
import {useState} from 'react';

const CommentForm = (props) => {
 const [commentForm, setComment] = usState ({
  comment: ``
 })

 const handleSubmit = (evt) => {
  evt.preventDefault();
}

const handleFieldChange = (evt) => {
 const {name, value} = evt.target;
 setComment({...commentForm, [name]: value});
}

const {comment} = commentForm;

 return (
  <form action="http://localhost:3000/annotation.html" method="post" className="comment-form" onSubmit={handleSubmit}>
                <div className="comment-inner">
                    <label className="comment-label" for="comment-input">Ваш комментарий</label>
                    <input type="text" className="comment-field" placeholder="Комментарий" required id="comment-input" name="comment" onChange={handleFieldChange}/>
                </div>
                <button className="comment-submit-button" type="submit">Отправить</button>
   </form>
 )
};

export default CommentForm;
