import React from 'react';

const Chapter = (props) => {

  const {reader, index, font} = props;
  const chapter = reader[1];

  const {
    chapterContent
  } = chapter;

  return (
    <article className="chapter-content" style={{fontSize: font + `px`}}>
      <div className="container">
        {chapter.chapterContent}
      </div>
    </article>
  );
};

export default Chapter;
