import React from 'react';
import Header from '../header/header';
import Footer from '../footer/footer';
import {useParams} from 'react-router-dom';
import Chapter from '../chapter/chapter';
import TableOfContents  from '../table-of-contents/table-of-contents';
import {useState, useEffect} from 'react';


const Reader = (props) => {

  const {readers} = props;
  const {id} = useParams();
  const reader = readers.find((reader) => +reader.id === +id);
  const {chapters} = reader;

const [selectForm, setSelectForm] = useState ({
  value: 0,
});

const handleFieldChange = (evt) => {
  const {value} = evt.target;
  setSelectForm({...selectForm, value});
  console.log('значение формы:', selectForm.value);

};





 return (
  <>
<div className="page light-theme">
	<div className="main">
  <Header />

    <div className="breadcrumbs">
		<a href="#">Главная</a>
		<a className="active" href="#">Замуж за коня</a>
		<a href="#">1-1</a>
	  </div>

    <div className="reader">
        <div className="controls">
            <div className="settings contents">
                <label>Оглавление:
                    <select name="content" onChange={handleFieldChange}>
                        <TableOfContents chapters={chapters} />
                    </select>
                </label>
            </div>
        <div className="settings size">
            <label> Размер текста: (<output name="pixels" className="pixels">14</output>px)
                <input name="sizesetting" className="size-setting" type="range" min="8" max="48" step="1" value="14"/>

            </label>          
        </div>
    </div>

        <div className="paginator">
            <ul>
                <li className="prev">
                    <a href="#prev">Назад</a>
                </li>
                <li className="first">
                    <a href="#1">1</a>
                </li>
                <li className="second">
                    <a href="#2">2</a>
                </li>
                <li className="third">
                    <a href="#3">3</a>
                </li>
                <li className="forth">
                    <a href="#4">4</a>
                </li>
                <li className="next">
                    <a href="#next">Вперёд</a>
                </li>
            </ul>
        </div>
        <article className="book-content">
            <Chapter chapters={chapters} index={index}/>
        </article>
    </div>


<Footer />

</div>
<button className="up-button" type="button">
    ↑
    <span className="visually-hidden">Наверх</span>
  </button>
</div>
</>
 )
}

export default Reader;
