import React from 'react';
import Header from '../header/header';
import Footer from '../footer/footer';
import {useParams} from 'react-router-dom';
import Chapter from '../chapter/chapter';
import TableOfContents  from '../table-of-contents/table-of-contents';
import {useState, useEffect} from 'react';
import {connect} from 'react-redux';
import {MAX_NUMBER_BUTTONS} from '../../common/const';
import {ButtonTypes} from '../../common/const';

const Reader = (props) => {

    const {
        onReaderDataRender,
        activeBookId,
        readers,
        fontSize,
      } = props;


  const reader = readers.find((reader) => reader.id === activeBookId);
  const {chapters} = reader;

const [selectForm, setSelectForm] = useState ({
  value: 1,
});

const handleChapterChange = (evt) => {
  const {value} = evt.target;
  setSelectForm({...selectForm, value});
};

const [rangeForm, setRangeForm] = useState ({
    value: 14,
  });

const handleFontSizeChange = (evt) => {
    const {value} = evt.target;
    setRangeForm({...rangeForm, value});
  };


useEffect(() => {

    console.log('Привет от UseEffect');

}, [selectForm]);




 return (
  <>
<div className="page light-theme">
	<div className="main">
  <Header />

    <div className="breadcrumbs">
		<a href="#">Главная</a>
		<a className="active" href="#">Замуж за коня</a>
		<a href="#">1-1</a>
	  </div>

    <div className="reader">
        <div className="controls">
            <div className="settings contents">
                <label>Оглавление:
                    <select name="content" onChange={handleChapterChange}>
                        <TableOfContents chapters={chapters} />
                    </select>
                </label>
            </div>
        <div className="settings size">
            <label> Размер текста: (<output name="pixels" className="pixels">{`${rangeForm.value}`}</output>px)
                <input name="sizesetting" className="size-setting" type="range" min="8" max="48" step="1" value={rangeForm.value} onChange={handleFontSizeChange}/>
            </label>          
        </div>
    </div>

        <div className="paginator">
            <ul>
                <li className="prev">
                    <a href="#prev">Назад</a>
                </li>
                {Object.values(ButtonTypes).slice(0, MAX_NUMBER_BUTTONS).map((ButtonTypes, id) => {
                  return (
                    <li className={ButtonTypes.TURN_PAGE} key={id}>
                      <a href="#1">{id+1}</a>
                    </li>
                  );
                })}
                <li className="next">
                    <a href="#next">Вперёд</a>
                </li>
            </ul>
        </div>
            <Chapter chapters={chapters}  index={selectForm.value} font={rangeForm.value}/>
    </div>


<Footer />

</div>
<button className="up-button" type="button">
    ↑
    <span className="visually-hidden">Наверх</span>
  </button>
</div>
</>
 );
};

const mapStateToProps = (state) => ({
    readers: state.readers,
    activeBookId: state.activeBookId,
    fontSize: state.fontSize,
  });

  const mapDispatchToProps = (dispatch) => ({
    onReaderDataRender(id) {
      dispatch(fetchReader(id));
    },
  });

export {Reader};
export default connect(mapStateToProps, mapDispatchToProps)(Reader);
