import {ActionType} from './action';
import {getReaderFromBook} from '../common/utils';
import {DEFAULT_BOOK} from '../common/const';
import {books} from '../mocks/books';


const initialState = {
  activeBook : DEFAULT_BOOK,
  books: []
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionType.CHANGE_BOOK:
      return {
        ...state,
        activeBook: action.payload
      };
    case ActionType.GET_READER:
      return {
        ...state,
        reader: action.payload,
        isDataLoaded: true
      };
     case ActionType.GET_BOOKS:
       return {
         ...state,
         books: getBooks(initialState.books)
       };
   };
  return state;
}

export {reducer};
