import {ActionType} from './action';
import {getBooks} from '../common/utils';
import {DEFAULT_BOOK} from '../common/const';
import books from '../mocks/books';


const initialState = {
  activeBook : DEFAULT_BOOK,
  books,
  reader: {},
  bookPage: {},
  isBookLoaded: false,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionType.CHANGE_BOOK:
      return {
        ...state,
        activeBook: action.payload
      };
    case ActionType.GET_READER:
      return {
        ...state,
        reader: action.payload,
        isDataLoaded: true
      };



     case ActionType.GET_BOOKS:
       return {
         ...state,
         books: initialState.books
       };
     case ActionType.GET_BOOK_PAGE:
       return {
         ...state,
         bookPage: action.payload,
         isBookLoaded: true
    };
   };


  return state;
}

export {reducer};
