import React from 'react';
import {AppRoute, AuthorizationStatus} from '../../common/const';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {getBook} from '../../common/utils.js';
import BookMenu from '../book-menu/book-menu';
import {ActionCreator} from "../../store/action";

const Header = (props) => {

const {
  books,
		setActiveBook, 
		unsetActiveBook
	} = props;


	return (
		<div className="page-header">
			<div className="nav">
				<ul className="main-menu">
					<li className="top"><Link to={AppRoute.MAIN}>Главная</Link></li>
					<li className="open"> <a href="#">Виктория ОМ</a>
						<ul className="sub-menu">
						  {books.map((book) => (
								  <BookMenu
												key={book.id}
												book={book}
												setActiveBook={setActiveBook}
												unsetActiveBook={unsetActiveBook}/>
								))}
						</ul>
					</li>
					<li><a href="#">Моя библиотека</a></li>
				</ul>
			</div>

			<div className="top-panel">
				<form action="" method="POST">
					<input type="search" name="search" id="search" placeholder="Найти" />
				</form>
				{authorizationStatus === AuthorizationStatus.AUTH ?
				  <>
						  <Link to={AppRoute.SUBSCRIPTIONS}>Вход
								  <span>{authorizationInfo.login}</span>
								</Link>
						</>
				  : <Link to={AppRoute.LOGIN}>Вход</Link>
    };

				: <Link to={AppRoute.LOGIN}>Вход</Link>
				<button className="theme-button" type="button">Изменить тему</button>
			</div>
		</div>
	)
};

const mapStateToProps = (state) => ({
	authorizationInfo: state.authorizationInfo,
	authorizationStatus: authorizationStatus
});

const mapDispatchToProps = (dispatch) => ({
  onLogoutClick() {
		  dispatch(logOut());
		}
});

export {Header};
export default connect(mapStateToProps, mapDispatchToProps)(Header);
