import {ActionCreator} from './action.js';
import {AuthorizationStatus, AppRoute, HttpCode, Status} from '../common/const.js';


export const checkAuth = () => (next, _getState, api) => (
  api.get(AppRoute.LOGIN)
     .then(({response}) => next(ActionCreator.authorizationInfo(response)))
     .then(() => next(ActionCreator.requireAuthorization(AuthorizationStatus.AUTH)))
     .catch(() => {})
);

export const logIn = ({userLogin: login, email, password}) => (next, _getState, api) => (
  api.post(AppRoute.LOGIN, {login, email, password})
    .then(({response}) => next(ActionCreator.authorizationInfo(response)))
    .then(() => next(ActionCreator.requireAuthorization(AuthorizationStatus.AUTH)))
    .then(() => next(ActionCreator.redirectToRoute(AppRoute.MAIN)))

);
